function Random({min, max,last,first=true}) {

    if(first){
        last[0] = Math.round(Math.random() * (max - min)) + min;
        return last[0]
    }
    
    last[1] = Math.round(Math.random() * (max - min)) + min;

    if(last[1]!=last[0]){
        last[0]=last[1]
        return last[1]
    }else{
        console.log("随机到上一次的值了哦",last[0]);
        return Random({min,max,last})
    }
    
}


export { Random }
