export const getArticleList = params =>{
    return request.get('/v1_0/articles',{
        params,
    })
}


// 获取文章详情

export const getArticleById = articleId =>{
    return request({
        method:"GET",
        url:`/v1_0/articles/${articleId}`
    })
}
