import request from "@/utils/request"

export const getComment = params =>{
    return request({
        method:"GET",
        url:"/v1_0/comments",
        params
    })
}

/**
 * 评论点赞
 */
 export const addCommentLike = target => {
    return req({
      method: 'POST',
      url: '/v1_0/comment/likings',
      data: {
        target
      }
    })
  }
  
  /**
   * 取消评论点赞
   */
  export const deleteCommentLike = target => {
    return req({
      method: 'DELETE',
      url: `/v1_0/comment/likings/${target}`
    })
  }
