function Random({min, max,last,first=false}) {

    if(first){
        last[0] = Math.round(Math.random() * (max - min)) + min;
        return last[0]
    }
    
    last[1] = Math.round(Math.random() * (max - min)) + min;

    if(last[1]!=last[0]){
        last[0]=last[1]
        return last[1]
    }else{
        return Random({min,max,last})
    }
    
}

const plugins = {
    begin:0,
    install(Vue){
        Vue.prototype.throttle = function(fn,delay){
            let curTime = Date.now();
            if(curTime - plugins.begin > delay){
                fn()
            }

        }
    }
}


export { Random }
