import vuex from "vuex"

import { getItem , setItem} from "@/utils/storage"

Vue.use(vuex)

const TOKEN_KEY  = "TOKEN_KEY";

const actions = {

}

const mutations = {
    setUser:(state,value)=>{
        state.user = setItem(TOKEN_KEY,value)
    }

}

const state = {
    user:getItem(TOKEN_KEY)
}

const getters = {
    
}

export default new vuex.Store({
    actions,
    mutations,
